A. FILE *file_ptr = fopen("data.txt", "r"); if (file_ptr) { /* Read data */ fclose(file_ptr); fclose(file_ptr); }
B. int *buffer = (int *)malloc(256 * sizeof(int)); if (buffer) { /* Use buffer */ free(buffer); }
